import React, { useState } from "react";

import { AppointmentForm } from "../../components/appointmentForm/AppointmentForm";
import { TileList } from "../../components/tileList/TileList";

export const AppointmentsPage = ({ appointmentsList, contactList, appointmentFn }) => {
  /*
  Define state variables for 
  appointment info
  */

  const [title, setTitle] = useState('');
  const [contact, setContact] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [currentDuplicate, setCurrentDuplicate] = useState(false);


  const handleSubmit = (e) => {
    e.preventDefault();
    /*
    Add contact info and clear data  
    */

    if(!currentDuplicate) {
      appointmentFn(title, contact, date, time)
      setTitle('');
      setContact('');
      setDate('');
      setTime('');
    }
   
  };

  return (
    <div>
      <section>
        <h2>Add Appointment</h2>
        <AppointmentForm contacts={contactList}
                         title={title}
                         setTitle={setTitle}
                         contact={contact}
                         setContact={setContact}
                         date={date}
                         setDate={setDate}
                         time={time}
                         setTime={setTime}
                        //  handleSubmit={handleSubmit} 
                        />
      </section>
      <hr />
      <section>
        <h2>Appointments</h2>
        <TileList appointmentArr={appointmentsList} />
      </section>
    </div>
  );
};
