export const ContactPicker = (props) => {
  const names = props.contacts.map((contact) => {
    return <option value={contact.name} key={contact.name}>{contact.name}</option>;
  });

  // Check if there's only one contact
  if (props.contacts.length === 1) {
    return (
      <form>
        <label htmlFor="contactList">Choose contact:</label>
        <select id="contactList" onChange={props.onChange} required>
          {/* Set the single contact as the default value */}
          <option value={props.contacts[0].name} selected>
            {props.contacts[0].name}
          </option>
        </select>
      </form>
    );
  }

  // If there are multiple contacts, render the select with all options
  return (
    <form>
      <label htmlFor="contactList">Choose contact:</label>
      <select id="contactList" onChange={props.onChange} required>
        {names}
      </select>
    </form>
  );
};
