import React, { useState } from "react";

import { AppointmentForm } from "../../components/appointmentForm/AppointmentForm";
import { TileList } from "../../components/tileList/TileList";

export const AppointmentsPage = ({ appointmentsList, contactList, appointmentFn }) => {
  /*
  Define state variables for 
  appointment info
  */

  const [currentTitle, setCurrentTitle] = useState('');
  const [currentContact, setCurrentContact] = useState('');
  const [currentDate, setCurrentDate] = useState('');
  const [currentTime, setCurrentTime] = useState('');
  const [currentDuplicate, setCurrentDuplicate] = useState(false);


  const handleSubmit = (e) => {
    e.preventDefault();
    /*
    Add contact info and clear data  
    */

    if(!currentDuplicate) {
      appointmentFn(currentTitle, currentContact, currentDate, currentTime)
      setCurrentTitle('');
      setCurrentContact('');
      setCurrentDate('');
      setCurrentTime('');
    }
   
  };

  return (
    <div>
      <section>
        <h2>Add Appointment</h2>
        <AppointmentForm contacts={contactList}
                         title={currentTitle}
                         setTitle={setCurrentTitle}
                         contact={currentContact}
                         setContact={setCurrentContact}
                         date={currentDate}
                         setDate={setCurrentDate}
                         time={currentTime}
                         setTime={setCurrentTime}
                         handleSubmit={handleSubmit} />
      </section>
      <hr />
      <section>
        <h2>Appointments</h2>
        <TileList appointmentsList={appointmentsList} />
      </section>
    </div>
  );
};
